    #huffhighlights { margin: 20px 0 10px 0; width:298px; overflow: hidden; border: #d3d3d3 solid 1px; display:none; }
    #huffhighlights_logo { background: url('/images/highlights/logo.png'); margin: 10px auto; width: 240px; height: 34px; cursor: pointer; }
    #huffhighlights_main { padding: 20px; height: 180px; overflow: hidden; }
    #huffhighlights_text { font: 22px Georgia, serif; line-height: 28px; color: #757575; cursor: pointer; display: inline-block; }
    .huffhighlights_long { font-size: 15px !important; line-height: 22px !important; }
    #huffhighlights_text:hover { color: #555; }
    #huffhighlights_author { height: 20px; margin-top: 20px; font:  bold 12px 'Helvetica Neue', Helvetica, Arial, sans-serif; cursor: pointer; display:none; }
    #huffhighlights_controls { background: #f7f7f7; height: 30px; line-height:25px; width: 100%; text-align: center; }
    .huffhighlights_control { color: #999999; cursor: pointer; font-family: Georgia; font-size: 14px; margin: 5px; } 
    .huffhighlights_control.active,
    .huffhighlights_control:hover { color: #555; }
    #huffhighlights_more { padding: 10px 0px 10px 40px; font: 14px Arial; }
    #huffhighlights_more a { cursor: pointer; color: #fff; }
