    #huffhighlights_more_vertical { font-style: italic; font-weight: bold; }

    
    .hl_text    { background-color: rgba(255, 255, 0, 0.4); }
    .hl_text:hover   { cursor: help; }

    
    .hl_flyout {
        display: none;
        position: absolute;
        width: 300px;
        font: 12px/normal "Helvetica Neue", Helvetica, Arial, sans-serif;
        color: #555;
        z-index: 10000;
        text-align: center;
        background: white;
        border: 1px solid #ccc;
        border-radius: 2px;
        box-shadow: rgba(0,0,0,.2) 0px 2px 2px;
    }

    .hl_flyout .top {
        padding-top: 10px;
    }

    .hl_flyout .middle {
        padding: 10px;
        font-size: 14px;
        line-height: 18px;
    }

    .hl_flyout .bottom {
        margin: 5px 0px 15px;
    }

    .hl_flyout .top img.logo {
        max-width: 200px;
    }

    .hl_flyout h1 {
        font-size: 24px;
        line-height: 26px;
        font-weight: normal;
        margin-bottom: 10px;
        display: block;
    }

    .hl_flyout .btn{
        width: auto;
        background-color: #555;
        border: 1px solid #333;
        color: white;
        cursor: pointer;
        display: inline-block;
        font-family: "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, "Lucida Grande", sans-serif;
        font-size: 12px;
        font-weight: bold;
        line-height: 1;
        margin: 0;
        outline: none;
        padding: 8px 12px;
        position: relative;
        text-align: center;
        text-decoration: none;
        -webkit-box-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) inset;
        -moz-box-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) inset;
        box-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) inset;
        -webkit-transition: background-color 0.15s ease-in-out;
        -moz-transition: background-color 0.15s ease-in-out;
        -o-transition: background-color 0.15s ease-in-out;
        transition: background-color 0.15s ease-in-out;
        -webkit-border-radius: 4px;
        -moz-border-radius: 4px;
        border-radius: 4px;
    }

    .hl_flyout .btn:hover {
        background-color: #333;
        text-decoration: none;
    }

    .hl_flyout .btn:active, .hightlight_this button:active {
        -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, 0.2) inset;
        -moz-box-shadow: 0 1px 0 rgba(0, 0, 0, 0.2) inset;
        box-shadow: 0 1px 0 rgba(0, 0, 0, 0.2) inset;
    }

    .highlight_this { position: absolute; display: none; }

    .highlight_this button {
        color: #666;
        font-family: "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, sans-serif;
        font-size: 12px;
        line-height: 10px;
        font-weight: bold;
        text-shadow: white 0 1px 2px;
        padding: 5px 11px 8px;
        cursor: pointer;
        border-width: 1px;
        border-style: solid; 
        border-color: #ccc #aaa #aaa #ccc;
        background: #eeeeee;
        background: -moz-linear-gradient(top,  #eeeeee 0%, #dadada 100%);
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#eeeeee), color-stop(100%,#dadada));
        background: -webkit-linear-gradient(top,  #eeeeee 0%,#dadada 100%);
        background: -o-linear-gradient(top,  #eeeeee 0%,#dadada 100%);
        background: -ms-linear-gradient(top,  #eeeeee 0%,#dadada 100%);
        background: linear-gradient(to bottom,  #eeeeee 0%,#dadada 100%);
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#eeeeee', endColorstr='#dadada',GradientType=0 );
        -webkit-border-radius: 4px;
        -moz-border-radius: 4px;
        border-radius: 4px;
        -webkit-box-shadow: rgba(0,0,0,.2) 0 1px 3px;
        -moz-box-shadow: rgba(0,0,0,.2) 0 1px 3px;
        box-shadow: rgba(0,0,0,.2) 0 1px 3px;
    }

    .highlight_this button:hover {
        background-color: #eee;
        border-color: #aaa;
        color: #555;
        z-index: 1000;
    }
    .hl_ht-plus {
        border-right: 1px solid white;
        padding-right: 8px;
        font-size: 18px;
        display: inline-block;
        height: 13px;
    }

    .hl_ht-text {
        border-left: 1px solid #ccc;
        padding-left: 8px;
    }

    .hl_tip {
        display: block;
        width: 10px;
        height: 7px;
        background: url('/images/highlights/tip.png') no-repeat top center;
        position: relative;
        top: 2px;
        left: 12px;
        z-index: 999;
    }

    .hl_bottom-tip {
        top: 34px !important;
        background-position: 0px -8px !important;
    }

    .close-flyout {
        position: absolute;
        display: inline-block;
        cursor: pointer;
        top: 4px;
        right: 8px;
        text-decoration:none;
        color: #888;
        font-size: 14px;
        line-height: 14px;
        font-weight: bold;
    }

    .close-flyout:hover {
        color: #555;
        text-decoration: none;
    }

